import React from "react";
import { Container, Card, ListGroup, Row, Col, Alert } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import { useEffect } from "react";
import { getAllOrders } from "../../redux/action/OrderAction";

// Функция для получения текста статуса
const getStatusText = (status) => {
    switch (status) {
        case 0:
            return "Виконано";
        case 1:
            return "В роботі";
        case 2:
            return "Відмінено";
        default:
            return "Невідомий статус";
    }
};

const ManageOrderPage = () => {
    const dispatch = useDispatch();
    const { orders = [], error } = useSelector((state) => state.order);

    useEffect(() => {
        dispatch(getAllOrders());
    }, [dispatch]);

    return (
        <Container className="my-5">
            <h2 className="mb-4">Manage Orders</h2>
            {error && <Alert variant="danger">{error}</Alert>}
            {orders.length === 0 ? (
                <Alert variant="info">No orders found</Alert>
            ) : (
                orders.map((order) => (
                    <Card key={order.id} className="mb-4">
                        <Card.Header>
                            <h5>Order ID: {order.id}</h5>
                            <small>User: {order.user.login} (ID: {order.user.id})</small>
                        </Card.Header>
                        <Card.Body>
                            <Row className="mb-3">
                                <Col>
                                    <strong>Status:</strong> {getStatusText(order.status)}
                                </Col>
                            </Row>
                            <ListGroup variant="flush">
                                {order.orderItems.map((item) => (
                                    <ListGroup.Item key={item.id}>
                                        <Row>
                                            <Col md={8}>
                                                <h6>{item.product.name}</h6>
                                                <small>Product ID: {item.product.id}</small>
                                            </Col>
                                            <Col md={2} className="text-center">
                                                Quantity: {item.productCount}
                                            </Col>
                                            <Col md={2} className="text-end">
                                                Price: {item.product.price.toLocaleString()} UAH
                                            </Col>
                                        </Row>
                                    </ListGroup.Item>
                                ))}
                            </ListGroup>
                        </Card.Body>
                        <Card.Footer className="text-end">
                            <strong>
                                Total Price:{" "}
                                {order.orderItems
                                    .reduce(
                                        (sum, item) =>
                                            sum + item.product.price * item.productCount,
                                        0
                                    )
                                    .toLocaleString()}{" "}
                                грн
                            </strong>
                        </Card.Footer>
                    </Card>
                ))
            )}
        </Container>
    );
};

export default ManageOrderPage;
