const initialState = {
    productCategoryId: null, 
    pageNumber: 1,
    sortBy: 0,
    products: null,
    hasNext: false,
    loading: false,
    error: null
};

export const productPageReducer = (state = initialState, action) => {
    console.log(`Action: ${action.type}`, action);
    console.log("State before action:", state);

    let newState;

    switch (action.type) {
        case 'GET_PRODUCT_PAGE_REQUEST':
            return {
                ...state,
                loading: true,
                error: null,
            };
        case 'GET_PRODUCT_PAGE_SUCCESS':
            return {
                ...state,
                currentProduct: action.newCurrentProduct,
                loading: false,
                error: null,
            };
        case 'GET_PRODUCT_PAGE_ERROR':
            return {
                ...state,
                loading: false,
                error: action.error,
            };
        case 'SET_SORT_TYPE':
            return {
                ...state,
                sortBy: action.newSortBy
            };
        default:
            newState = state;
    }

    // Логування нового стану
    console.log("State after action:", newState);

    return newState;
};
