import { useSelector, useDispatch } from "react-redux";
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';
import { clearBasket } from '../../redux/action/BasketAction'
import BasketItem from "./BasketItem";
import { useEffect, useState } from "react";

const Basket = () => {
    const dispatch = useDispatch();
    const { products } = useSelector((state) => state.basket);
    const [ totalSum, setTotalSum ] = useState(totalSum)

    console.log("Basket Basket Basket Basket Basket Basket")
    console.log(products)


    useEffect(() => {
        let totalSum = 0;
        products.forEach((p) => { totalSum += p.price * p.count });
    }, [products])

    if (products.length === 0) {
        return (
            <Container 
                className="d-flex justify-content-center align-items-center" 
                style={{ height: '100vh' }}
            >
                <h1>Кошик пустий!</h1>
            </Container>
        );
    } else {
        return (
            <Container>
                {
                    products.map((product)=>
                    {
                        return (<BasketItem product={product} />);
                    })
                }
                <div style={{ height: "3px", backgroundColor: "gray", margin: "20px 0px"}}></div>
                <p>
                    Разом: 
                </p>
                <Button onClick={() => { dispatch(clearBasket()) }}>Очистити кошик</Button>
            </Container>
        );
    }
};

export default Basket;
