import { Container, Card, ListGroup, Row, Col, Alert } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import { useEffect } from "react";
import { getAllOrders } from "../../redux/action/OrderAction";

const ManageOrderPage = () => {
    const dispatch = useDispatch();
    const { orders = [], error } = useSelector((state) => state.order);

    useEffect(() => {
        dispatch(getAllOrders());
    }, [dispatch]);

    const getStatusText = (status) => {
        switch (status) {
            case 0:
                return "Виконано";
            case 1:
                return "В роботі";
            case 2:
                return "Відмінено";
            default:
                return "Невідомий статус";
        }
    };

    return (
        <Container className="py-4">
            <h1 className="mb-4">Керування замовленнями</h1>

            {error && <Alert variant="danger">{error}</Alert>}
            {orders.length > 0 ? (
                orders.map((order) => {
                    if (!order || !order.orderItems || !order.user) return null;

                    const totalAmount = order.orderItems.reduce((sum, item) => {
                        if (!item.product) return sum;
                        return sum + item.product.price * item.productCount;
                    }, 0);

                    return (
                        <Card key={order.id} className="mb-4">
                            <Card.Header>
                                <h5>
                                    Order ID: {order.id} | User: {order.user.login} (ID: {order.user.id})
                                </h5>
                            </Card.Header>
                            <Card.Body>
                                <ListGroup variant="flush">
                                    {order.orderItems.map((item) => (
                                        <ListGroup.Item key={item.id}>
                                            <Row>
                                                <Col md={6}>
                                                    <h5>
                                                        <a href={`/product/${item.product.id}`}>
                                                            {item.product.name}
                                                        </a>
                                                    </h5>
                                                </Col>
                                                <Col md={2}>
                                                    Кількість: {item.productCount}
                                                </Col>
                                                <Col md={2}>
                                                    <strong>{item.product.price} грн</strong>
                                                </Col>
                                            </Row>
                                        </ListGroup.Item>
                                    ))}
                                </ListGroup>
                            </Card.Body>
                            <Card.Footer>
                                Загальна сума: {totalAmount} грн | Статус: {getStatusText(order.status || 1)}
                            </Card.Footer>
                        </Card>
                    );
                })
            ) : (
                <Alert variant="info">
                    <h4>No orders found.</h4>
                </Alert>
            )}
        </Container>
    );
};

export default ManageOrderPage;
