import React, { useState } from 'react';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';

const RegistrationPage = () => {
  const [formData, setFormData] = useState({
    login: '',
    password: '',
    email: ''
  });

  const [error, setError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Проверка длины пароля
    if (formData.password.length < 6) {
      setError('Пароль должен содержать минимум 6 символов');
      return;
    }

    // Очистка ошибок, если все в порядке
    setError('');

    // Отправка данных, например, в backend
    console.log(formData);
  };

  return (
    <Container className="my-5">
      <h2 className="mt-3 mb-3">Реєстрація</h2>
      <Form onSubmit={handleSubmit}>
        <Row>
          <Col>
            <Form.Group controlId="login">
              <Form.Label className="mt-2">Login</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter your login"
                name="login"
                value={formData.login}
                onChange={handleChange}
                required
              />
            </Form.Group>
          </Col>
        </Row>

        <Row>
          <Col>
            <Form.Group controlId="email">
              <Form.Label className="mt-2">Email address</Form.Label>
              <Form.Control
                type="email"
                placeholder="Enter your email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </Form.Group>
          </Col>
        </Row>

        <Row>
          <Col>
            <Form.Group controlId="password">
              <Form.Label className="mt-2">Password</Form.Label>
              <Form.Control
                type="password"
                placeholder="Enter your password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                minLength={6}  // минимальная длина пароля
                required
              />
            </Form.Group>
          </Col>
        </Row>

        {error && <p className="text-danger">{error}</p>}  {/* Показываем ошибку, если она есть */}

        <Button variant="primary" type="submit" className="mt-3">
          Register
        </Button>
      </Form>
    </Container>
  );
};

export default RegistrationPage;
