import React from "react";
import { Container, Card, ListGroup, Row, Col, Alert } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import { useEffect } from "react";
import { getAllOrders } from "../../redux/action/OrderAction";

const ManageOrderPage = () => {
    const dispatch = useDispatch();
    const { orders, error } = useSelector((state) => state.order);

    useEffect(() => {
        dispatch(getAllOrders());
    }, [dispatch]);

    return (
        <Container className="my-5">
            <h2 className="mb-4">Manage Orders</h2>
            {error && <Alert variant="danger">{error}</Alert>}
            <Row>
                {orders && orders.length > 0 ? (
                    orders.map((order) => (
                        <Col key={order.id} md={4} className="mb-3">
                            <Card>
                                <Card.Header>Order ID: {order.id}</Card.Header>
                                <ListGroup variant="flush">
                                    <ListGroup.Item>User: {order.user.name}</ListGroup.Item>
                                    <ListGroup.Item>
                                        Items:
                                        <ul>
                                            {order.orderItems.map((item, index) => (
                                                <li key={index}>{item.name} (x{item.quantity})</li>
                                            ))}
                                        </ul>
                                    </ListGroup.Item>
                                </ListGroup>
                            </Card>
                        </Col>
                    ))
                ) : (
                    <Alert variant="info">No orders found.</Alert>
                )}
            </Row>
        </Container>
    );
};

export default ManageOrderPage;
