import { Container, Card, ListGroup, Row, Col, Alert } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import { useEffect } from "react";
import { getAllOrders } from "../../redux/action/OrderAction";

const ManageOrderPage = () => {
    const dispatch = useDispatch();
    const { orders = [], error } = useSelector((state) => state.order);

    useEffect(() => {
        dispatch(getAllOrders());
    }, [dispatch]);

    const calculateTotalAmount = (orderItems) => {
        return orderItems.reduce((sum, item) => {
            return sum + (item.product?.price || 0) * (item.productCount || 0);
        }, 0);
    };

    return (
        <Container className="py-4">
            <h1 className="mb-4">Керування замовленнями</h1>

            {error && <Alert variant="danger">{error}</Alert>}
            {orders.length > 0 ? (
                orders.map((order) => {
                    // Проверка, что все данные присутствуют
                    if (!order || !order.user || !order.orderItems) return null;

                    const totalAmount = calculateTotalAmount(order.orderItems);

                    return (
                        <Card key={order.id} className="mb-4">
                            <Card.Header>
                                <h5>
                                    Order ID: {order.id} | User: {order.user.login} (ID: {order.user.id})
                                </h5>
                            </Card.Header>
                            <Card.Body>
                                <ListGroup variant="flush">
                                    {order.orderItems.map((item) => (
                                        <ListGroup.Item key={item.id}>
                                            <Row>
                                                <Col md={6}>
                                                    <h5>
                                                        <a href={`/product/${item.product?.id}`}>
                                                            {item.product?.name || "Невідомий продукт"}
                                                        </a>
                                                    </h5>
                                                </Col>
                                                <Col md={3}>
                                                    Кількість: {item.productCount}
                                                </Col>
                                                <Col md={3}>
                                                    <strong>{item.product?.price || 0} грн</strong>
                                                </Col>
                                            </Row>
                                        </ListGroup.Item>
                                    ))}
                                </ListGroup>
                            </Card.Body>
                            <Card.Footer>
                                Загальна сума: {totalAmount} грн
                            </Card.Footer>
                        </Card>
                    );
                })
            ) : (
                <Alert variant="info">
                    <h4>No orders found.</h4>
                </Alert>
            )}
        </Container>
    );
};

export default ManageOrderPage;
