import React, { useState } from 'react';
import { Container, Button, Form } from 'react-bootstrap';

const AddProduct = () => {
    const { categories } = useSelector((state) => state.productCategory);


    // Стан для кожного поля форми
    const [product, setProduct] = useState({
        name: '',
        fullDescription: '',
        shortDescription: '',
        price: '',
        mainImage: '',
        productCategoryId: ''
    });

    // Обробник зміни значень у полях форми
    const handleChange = (e) => {
        const { name, value } = e.target;
        setProduct((prevProduct) => ({
            ...prevProduct,
            [name]: value
        }));
    };

    // Обробник зміни для поля зображення
    const handleImageChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                // Зберігаємо зображення як base64 строку
                setProduct((prevProduct) => ({
                    ...prevProduct,
                    mainImage: reader.result
                }));
            };
            reader.readAsDataURL(file); // Конвертуємо файл в base64
        }
    };

    // Обробник відправки форми
    const handleSubmit = (e) => {
        e.preventDefault();
        // Тут буде логіка відправки даних на сервер
        console.log(product);
    };

    return (
        <Container>
            <h1 className='mt-3 mb-4'>Додати товар</h1>
            <Form onSubmit={handleSubmit}>
                {/* Поле Назва товару */}
                <Form.Group className="mb-3" controlId="formProductName">
                    <Form.Label>Назва товару:</Form.Label>
                    <Form.Control
                        type="text"
                        placeholder="Введіть назву товару"
                        name="name"
                        value={product.name}
                        onChange={handleChange}
                        required
                    />
                </Form.Group>

                {/* Поле Повне опис товару */}
                <Form.Group className="mb-3" controlId="formFullDescription">
                    <Form.Label>Повне опис товару:</Form.Label>
                    <Form.Control
                        as="textarea"
                        rows={4}
                        placeholder="Введіть повне опис товару"
                        name="fullDescription"
                        value={product.fullDescription}
                        onChange={handleChange}
                        required
                    />
                </Form.Group>

                {/* Поле Коротке опис товару */}
                <Form.Group className="mb-3" controlId="formShortDescription">
                    <Form.Label>Коротке опис товару:</Form.Label>
                    <Form.Control
                        as="textarea"
                        rows={3}
                        placeholder="Введіть коротке опис товару"
                        name="shortDescription"
                        value={product.shortDescription}
                        onChange={handleChange}
                        required
                    />
                </Form.Group>

                {/* Поле Ціна товару */}
                <Form.Group className="mb-3" controlId="formPrice">
                    <Form.Label>Ціна товару:</Form.Label>
                    <Form.Control
                        type="number"
                        step="0.01"
                        placeholder="Введіть ціну товару"
                        name="price"
                        value={product.price}
                        onChange={handleChange}
                        required
                    />
                </Form.Group>

                {/* Поле Зображення товару */}
                <Form.Group className="mb-3" controlId="formMainImage">
                    <Form.Label>Зображення товару:</Form.Label>
                    <Form.Control
                        type="file"
                        accept="image/*"
                        onChange={handleImageChange}
                    />
                </Form.Group>

                {/* Поле ID категорії товару */}
                <Form.Group className="mb-3" controlId="formProductCategoryId">
                    <Form.Label>ID категорії товару:</Form.Label>
                    <Form.Control
                        type="number"
                        placeholder="Введіть ID категорії товару"
                        name="productCategoryId"
                        value={product.productCategoryId}
                        onChange={handleChange}
                    />
                </Form.Group>

                {/* Кнопка відправки форми */}
                <Button variant="primary" type="submit">
                    Додати товар
                </Button>
            </Form>
        </Container>
    );
}

export default AddProduct;
