import { Container } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import { useEffect } from "react";
import { getOrdersByUserId } from "../../redux/action/OrderAction";
import { useNavigate } from 'react-router-dom';

const OrderPage = () => {
    const dispatch = useDispatch();
    const navigate = useNavigate();

    const { orders, error } = useSelector((state) => state.order);
    const { currentUser } = useSelector((state) => state.user);

    useEffect(() => {
        if (currentUser) {
            dispatch(getOrdersByUserId(currentUser.id));
        }

    }, [currentUser, dispatch]);

    useEffect(() => {
        console.log("Orders:", orders);
    }, [orders]);

    

    return (
        <Container>
            {orders?.length > 0 ? (
                orders.map(order => (
                    <div key={order.id}>
                        <h2>Order ID: {order.id}</h2>
                        {order.orderItems.map(item => (
                            <div key={item.product.id}>
                                <h3>{item.product.name}</h3>
                                <p>Quantity: {item.productCount}</p>
                                <p>Price: {item.product.price} грн</p>
                            </div>
                        ))}
                    </div>
                ))
            ) : (
                <h4>No orders found.</h4>
            )}
        </Container>
    );
};

export default OrderPage;
