import { useSelector, useDispatch } from "react-redux";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';
import { clearBasket } from '../../redux/action/BasketAction'
import BasketItem from "./BasketItem";
import { useEffect, useState } from "react";
import { useNavigate } from 'react-router-dom';

const Basket = () => {
    const dispatch = useDispatch();
    
    const navigate = useNavigate();

    const [ totalSum, setTotalSum ] = useState(0);
    const { products } = useSelector((state) => state.basket);
    const { currentUser } = useSelector((state) => state.user);

    console.log("Basket Basket Basket Basket Basket Basket")
    console.log(products)

    useEffect(() => {
        let _totalSum = 0;
        products.forEach((p) => 
        { 
            _totalSum += p.price * p.count 
        });

        setTotalSum(_totalSum)
    }, [products])
    

    const createOrder = () => 
    {
        if (currentUser)
        {
            const order = 
            {
                products,
                user: currentUser.login
            }

            console.log("ORDER ORDER ORDER")
            console.log(order)
        }
        else
        {
            navigate(`/authorization`);
        }
    }

    if (products.length === 0) { //Пустий кошик
        return (
            <Container 
                className="d-flex justify-content-center align-items-center" 
                style={{ height: '100vh' }}
            >
                <h1>Кошик пустий!</h1>
            </Container>
        );
    } 
    else 
    {
        return (
            <Container>
                {
                    products.map((product)=>
                    {
                        return (<BasketItem product={product} />);
                    })
                }
                <div style={{ height: "3px", backgroundColor: "gray", margin: "20px 0px"}}></div>
                <p className="d-flex justify-content-end">
                    Разом: {totalSum} грн. 
                </p>
                <Row>
                    <Button className="mt-1" variant="warning" onClick={() => { dispatch(clearBasket()) }}>Очистити кошик</Button>
                    <Button className="mt-1" variant="success" onClick={() => { createOrder() }}>Створити замовлення</Button>
                </Row>
            </Container>
        );
    }
};

export default Basket;
