import { Container } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import { useEffect, useState } from "react";
import { getOrdersByUserId } from "../../redux/action/OrderAction"
import { useNavigate } from 'react-router-dom';
import Row from 'react-bootstrap/Row';

const OrderPage = () => {
    const dispatch = useDispatch();
    const navigate = useNavigate();

    const { orders, error } = useSelector((state) => state.order);
    const { currentUser } = useSelector((state) => state.user);

    useEffect(() => {
        if (currentUser !== null)
        {
            dispatch(getOrdersByUserId(currentUser?.id))
        }
    }, [currentUser])

    useEffect(() => {
        console.log("orders orders orders orders orders")
        console.log(orders)
    }, [orders])

    return (
        <>
            <Container>
                {
                    orders.forEach(order => {
                        order.orderItems.forEach(item => 
                        {
                            return item.name
                        })
                    })
                };

                <h1>{orders[0].orderItems[0].product.name}</h1>
            </Container>
        </>
    );
}

export default OrderPage;
