import React, { useEffect } from 'react';
import { useDispatch} from 'react-redux';
import { useSelector } from 'react-redux';
import { fetchProductCategories } from '../../redux/action/ProductCategoryAction'; // Импортируем экшн для загрузки продуктов

const ProductCategories = () => {
    console.log('ProductCategories component rendered');

  const dispatch = useDispatch();
  const { products, loading, error } = useSelector((state) => state.productCategory);

  // Загружаем продукты при монтировании компонента
  useEffect(() => {
    console.log('useEffect triggered');
    dispatch(fetchProductCategories());
  }, [dispatch]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  // Отображаем отфильтрованные продукты или все продукты, если фильтрация не активирована
  return (
    // <div>
    //   <h1>Products</h1>
    //   <ul>
    //     {products?.map((product) => (
    //       <li key={product.id}>
    //         {product.name}
    //       </li>
    //     ))}
    //   </ul>
    // </div>
    <>
        <ProductCategoriesCard />
    </>
  );
};

export default ProductCategories;
