import { useSelector, useDispatch } from "react-redux";
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';
import Image from 'react-bootstrap/Image';
import { clearBasket } from '../../redux/action/BasketAction'

const BasketItem = ({product}) => {
    const dispatch = useDispatch();
    const { products } = useSelector((state) => state.basket);

    console.log("Basket Basket Basket Basket Basket Basket")
    console.log(products)

    return (
        <Row className="align-items-center my-3">
            <Col md={2} className="text-center">
                <Image 
                    src={product.mainImage} 
                    rounded 
                    style={{ maxWidth: '150px', height: 'auto' }} 
                    alt={product.name} 
                />
            </Col>
            <Col md={5} className="d-flex flex-column">
                <h5 className="mb-1">{product.name}</h5>
                <span className="text-muted">{product.price} грн</span>
            </Col>
            <Col md={5} className="d-flex align-items-center justify-content-center">
                <Button variant="outline-secondary" size="sm" className="me-2">-</Button>
                <span className="mx-2">{product.count}</span>
                <Button variant="outline-secondary" size="sm" className="ms-2">+</Button>
            </Col>
    </Row>
    );
};

export default BasketItem;
