// productReducer.js
const initialState = {
    categories: [],               // Список продуктов
    currentCategory,
    loading: false,             // Состояние загрузки
    error: null                // Ошибка (если есть)
};

export const productCategoryReducer = (state = initialState, action) => {
    console.log(`Action: ${action.type}`, action);
    console.log("State before action:", state);

    switch (action.type) {
        // Запрос на загрузку продуктов
        case 'FETCH_PRODUCTS_REQUEST':
            return {
                ...state,
                loading: true,
            };

        // Продукты успешно загружены
        case 'FETCH_PRODUCTS_SUCCESS':
            return {
                ...state,
                loading: false,
                categories: action.payload,  // Обновляем список продуктов
                currentCategory: categories[0],
            };
        // Ошибка при загрузке продуктов
        case 'FETCH_PRODUCTS_FAILURE':
            return {
                ...state,
                loading: false,
                error: action.error,  // Сохраняем ошибку
            };

        default:
            return state;  // Возвращаем текущий state по умолчанию, если экшен не найден
    }
};
