import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';

const ProductCard = ({product}) => 
{
    return (
        <Card style={{ width: '17rem', margin: '5px', minWidth: '305px' }}>
            <Card.Img variant="top" src={"data:image/png;base64," + product.image} />
            <Card.Body>
            <Card.Title>{product.name}</Card.Title>
            <Card.Text style={{ textAlign: 'justify' }}>{product.shortDescription}</Card.Text>
            <Button variant="primary w-100">Детальніше</Button>
            </Card.Body>
        </Card>
    );
};

export default ProductCard;
