import { useSelector } from "react-redux";
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import Image from 'react-bootstrap/Image';
import Row from 'react-bootstrap/Row';
import { useState } from "react";

const ProductsPage = () => 
{
    const {currentCategory} = useSelector((state) => state.productCategory)
    const [sortType, setSortType] = useState(0)

    const funcHandleOnChangeSortType = (e)=>
    {
        console.log("e = ", sortType)
    };

    return (
        <Container >
            <Row>
                <Col md={9}>
                    <h1 id="productsHeadline" className="mt-4 mb-4">{currentCategory?.name}</h1>
                </Col>
                <Col md={3}>
                    <Form.Select className="mt-5 mb-4" onChange={funcHandleOnChangeSortType} value={sortType}>
                        <option value="0">Сортування за зростанням ціни</option>
                        <option value="1">Сортування за спаданням ціни</option>
                    </Form.Select>
                </Col>
            </Row>
            
        </Container>
    );
};

export default ProductsPage;
