import React, { useEffect } from 'react';
import { useDispatch} from 'react-redux';
import { useSelector } from 'react-redux';
import { fetchProducts } from '../../redux/action/ProductCategoryAction'; // Импортируем экшн для загрузки продуктов

const ProductCategories = () => {
  const dispatch = useDispatch();
  const { products, loading, error, filtered_products } = useSelector((state) => state.products);

  // Загружаем продукты при монтировании компонента
  useEffect(() => {
    dispatch(fetchProducts());
  }, [dispatch]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  // Отображаем отфильтрованные продукты или все продукты, если фильтрация не активирована
  const displayProducts = filtered_products.length ? filtered_products : products;

  return (
    <div>
      <h1>Products</h1>
      <ul>
        {displayProducts.map((product) => (
          <li key={product.id}>
            {product.name} - ${product.price}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ProductCategories;
