import React, { useEffect } from 'react';
import { useDispatch} from 'react-redux';
import { useSelector } from 'react-redux';
import { fetchProductCategories } from '../../redux/action/ProductCategoryAction';
import ProductCategoriesCard from './ProductCategorieCard.jsx';

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';


const ProductCategories = () => {
    console.log('ProductCategories component rendered');

  const dispatch = useDispatch();
  const { products, loading, error } = useSelector((state) => state.productCategory);

  // Загружаем продукты при монтировании компонента
  useEffect(() => {
    console.log('useEffect triggered');
    dispatch(fetchProductCategories());
  }, [dispatch]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  // Отображаем отфильтрованные продукты или все продукты, если фильтрация не активирована
  return (
    <Container>
        <Row>
            <Col></Col>
            <Col></Col>
            <Col></Col>
            <ProductCategoriesCard />
        </Row>
    </Container>
  );
};

export default ProductCategories;

