export const registerNewUser = (user) => {
    return async (dispatch) => {
        console.log('Calling API to register user');
        try {
            const response = await fetch('https://localhost:7106/api/auth/register', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(user),
            });

            // Check if response is successful
            if (!response.ok) {
                console.error("response.statusText: ", response.body) //как тут вывести содержимое body
                throw new Error(`Error: ${response.statusText}`);
            }

            const userFromDb = await response.json();
            console.log("RegisterNewUser RESPONSE:", userFromDb);

            dispatch({
                type: 'REGISTER_NEW_USER',
                payload: userFromDb,
            });
        } catch (error) {
            console.error('Error creating user:', error); // Log any errors
        }
    };
};
