import React, { useEffect } from 'react';
import { useDispatch} from 'react-redux';
import { useSelector } from 'react-redux';
import { fetchProductCategories } from '../../redux/action/ProductCategoryAction';
import ProductCategoriesCard from './ProductCategoriesCard.jsx';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';

const ProductCategories = () => {
    console.log('ProductCategories component rendered');

  const dispatch = useDispatch();
  const { categories, loading, error } = useSelector((state) => state.productCategory);

  // Загружаем продукты при монтировании компонента
  useEffect(() => {
    console.log('useEffect triggered');
    dispatch(fetchProductCategories());
  }, [dispatch]);

  if (loading) return <div class="d-flex justify-content-center">Loading categories...</div>;
  if (error) return <div class="d-flex justify-content-center">Error occured while fetching categories: {error}</div>;

  console.log('categories[0]')
  console.log(categories[0])

  // Отображаем отфильтрованные продукты или все продукты, если фильтрация не активирована
  if (categories.length > 0)
  {
    return (
      <Container>
          <Row>
            <div class="d-flex align-content-center flex-wrap">
                <ProductCategoriesCard category={categories[0]}/>
                <ProductCategoriesCard category={categories[1]}/>
                <ProductCategoriesCard category={categories[2]}/>
                <ProductCategoriesCard category={categories[3]}/> 
            </div>
          </Row>
      </Container>
    );
  }
};

export default ProductCategories;

